PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE users (
	id INTEGER NOT NULL, 
	username VARCHAR, 
	password_hash VARCHAR, 
	email VARCHAR, 
	created_at DATETIME, is_admin INTEGER DEFAULT 0, 
	PRIMARY KEY (id)
);
CREATE TABLE chat_history (
	id INTEGER NOT NULL, 
	user_id INTEGER, 
	question TEXT NOT NULL, 
	answer TEXT NOT NULL, 
	timestamp DATETIME, document_id INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE TABLE documents (
	id INTEGER NOT NULL, 
	owner_id INTEGER NOT NULL, 
	path VARCHAR, 
	title VARCHAR, 
	uploaded DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(owner_id) REFERENCES users (id)
);
CREATE TABLE password_reset_tokens (
	id INTEGER NOT NULL, 
	user_id INTEGER NOT NULL, 
	token_hash VARCHAR(64) NOT NULL, 
	expires_at DATETIME NOT NULL, 
	used_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(user_id) REFERENCES users (id)
);
CREATE INDEX ix_users_id ON users (id);
CREATE UNIQUE INDEX ix_users_username ON users (username);
CREATE INDEX ix_chat_history_id ON chat_history (id);
CREATE UNIQUE INDEX uq_users_email_not_null
ON users(email)
WHERE email IS NOT NULL AND trim(email) <> '';
CREATE UNIQUE INDEX ix_password_reset_tokens_token_hash ON password_reset_tokens (token_hash);
CREATE INDEX ix_password_reset_tokens_user_id ON password_reset_tokens (user_id);
COMMIT;
